import React, { useState, useEffect,useReducer } from 'react'
import List from './List'
import Alert from './Alert'

const getLocalStorage = () => {
  let list = localStorage.getItem('list')
  if(list) {
    return JSON.parse(localStorage.getItem('list'))
  }
  else {
    return defaultState;
  }
}

const reducer = (state, action) => {

   if(action.type === 'DISPLAY_ALERT') {
     return  {...state,  alert: {show: true, msg: 'please enter value', type: 'danger'}}
   }
  if(action.type === 'DISPLAY_ALERT') {
    return  {...state,  alert: {show: true, msg: 'item added to the list', type: 'success'}}
  }
  return state
}
const defaultState = {
  list: getLocalStorage(),
  isEditing: false,
  editId: null,
  alert: {show: false, msg: '', type: ''},
  areYouSure: false
}

function App() {
  const [name, setName] = useState('');
  const[state, dispatch] = useReducer(reducer, defaultState)
  // const [list, setList] = useState(getLocalStorage());
  // const [isEditing, setIsEditing] = useState(false);
  // const [editId, setEditId] = useState(null);
  // const [alert, setAlert] = useState({show: false, msg: '', type: ''});
  // const [areYouSure, setAreyouSure] = useState(false)


  const handleSubmit = (e) => {
    e.preventDefault();
    if(!name) {
    //  display alert
    //   showAlert(true,'danger','please enter value')
      dispatch({type: 'DISPLAY_ALERT'})
    } else if (name && state.isEditing) {
    //  deal with edit
    //   state.list = (state.list.map((item) => {
    //     if(item.id === state.editId) {
    //       return {...item, title: name}
    //     }
    //     return item
    //   }))
    //   state.name('')
    //   state.editId(false)
    //   state.editId(null)
    //   showAlert(true,'success', 'value changed')
    } else {
    //  show alert
    //   showAlert(true, 'success', 'item added to the list')
    //   const newItem = {id: new Date().getTime().toString(), title: name}
    //   state.list([...state.list, newItem])
    //   setName('')
      dispatch({type: 'ADD_ITEM'})
    }

  }
  const showAlert = (show = false, type = '', msg='') => {
    state.alert({show, msg, type})
  }
  const clearlist = () => {
    showAlert(true, 'danger', 'empty list')
    state.list([])
  }
  const removeItem = (id) => {
      showAlert(true, 'danger', 'item removed')
      state.list(state.list.filter((item) => item.id !== id))
  }
  const editItem = (id) => {
      const specificItem = state.list.find((item) => item.id === id)
      state.isEditing(true);
      state.editId(id)
      setName(specificItem.title)
  }
  useEffect(() => {
    localStorage.setItem('list',JSON.stringify(state.list))
  },[state.list])

  return <section className={'section-center'}>
    <form onSubmit={handleSubmit} className="grocery-form">
      {state.alert.show && <Alert {...state.alert} removeAlert={showAlert} list={state.list}/>}
      <h3>reminder</h3>
      <div className="form-control">
        <input type="text" className={'grocery'} placeholder={'enter your reminder'}
               value={name}
               onChange={(e) => setName(e.target.value)}/>
        <button type={'submit'} className={'submit-btn'}>{state.isEditing? 'edit': 'submit'}</button>
      </div>
    </form>
    <div className="grocery-container">
      <List items={state.list} removeItem={removeItem} editItem={editItem}/>
      {/*<button className={'clear-btn'} onClick={clearlist}>clear items</button>*/}
    </div>
  </section>
}


export default App
