import React, { useState, useEffect,useReducer } from 'react'
import List from './List'
import Alert from './Alert'



const reducer = (state, action) => {
   if(action.type === 'DISPLAY_ALERT') {
     return  {...state,  alert: {show: true, msg: 'please enter value', type: 'danger'}}
   }
  if(action.type === 'ADD_ITEM') {
    const newList = [...state.list, action.payload]
    console.log(newList)
    return  {...state,
        alert: {show: true, msg: 'item added to the list', type: 'success'},
        list: newList
    }
  }
  if(action.type === 'HIDE_ALERT') {
    return {...state, alert: {show: false, msg: '', type: ''}}
  }
  if(action.type === 'REMOVE_ITEM') {
    return {...state, list: action.payload}
  }
  throw new Error('no matching action type')
}
const defaultState = {
  list: [],
  isEditing: false,
  editId: null,
  alert: {show: false, msg: '', type: ''},
  areYouSure: false,
  background: false,

}

function App() {
  const [name, setName] = useState('');
  const[state, dispatch] = useReducer(reducer, defaultState)
  // const [list, setList] = useState(getLocalStorage());
  // const [isEditing, setIsEditing] = useState(false);
  // const [editId, setEditId] = useState(null);
  // const [alert, setAlert] = useState({show: false, msg: '', type: ''});
  // const [areYouSure, setAreyouSure] = useState(false)


  const handleSubmit = (e) => {
    e.preventDefault();
    if(!name) {
    //  display alert
    //   showAlert(true,'danger','please enter value')
      dispatch({type: 'DISPLAY_ALERT'})
    }
    if (name && state.isEditing) {
    //  deal with edit
    //   state.list = (state.list.map((item) => {
    //     if(item.id === state.editId) {
    //       return {...item, title: name}
    //     }
    //     return item
    //   }))
    //   state.name('')
    //   state.editId(false)
    //   state.editId(null)
    //   showAlert(true,'success', 'value changed')
    }
    if (name && !state.isEditing){
    //  show alert
    //   showAlert(true, 'success', 'item added to the list')
    //   const newItem = {id: new Date().getTime().toString(), title: name}
    //   state.list([...state.list, newItem])
    //   setName('')
      let  newItem = {id: new Date().getTime().toString(), title: name}
      dispatch({type: 'ADD_ITEM', payload: newItem})
     setName(() => {
       return ''
     })
    }
    console.log(state.list)
  }

  const removeAlert = () => {
    dispatch({type: 'HIDE_ALERT'})
  }
  const removeItem = (id) => {
    const filterList = state.list.filter((item) => item.id !== id)
    dispatch({type: 'REMOVE_ITEM', payload: filterList})
  }
  const editItem= (id) => {
    const editedItem = state.list.find((item) => item.id === id)
    setName(editedItem.title)
    dispatch({type: 'REMOVE_ITEM', payload: editedItem})
  }


  // const editItem = (id) => {
  //     const specificItem = state.list.find((item) => item.id === id)
  //     state.isEditing(true);
  //     state.editId(id)
  //     setName(specificItem.title)
  // }

  useEffect(() => {
    localStorage.setItem('newlist',JSON.stringify(state.list))
  },[state.list])

  return <>
    {state.alert.show && <Alert {...state.alert} removeAlert={removeAlert} list={state.list}/>}
    <section className={`${ state.background ? 'section-center backdround': 'section-center'}`}>
      <form onSubmit={handleSubmit} className="grocery-form">
        <h3>reminder</h3>
        <div className="form-control">
          <input type="text" className={'grocery'} placeholder={'enter your reminder'}
                 value={name}
                 onChange={(e) => setName(e.target.value)}/>
          <button type={'submit'} className={'submit-btn'}>{state.isEditing? 'edit': 'submit'}</button>
        </div>
      </form>
      <div className="reminder-container">
        <List items={state.list} removeItem={removeItem} editItem={editItem} />
        {/*<button className={'clear-btn'} onClick={clearlist}>clear items</button>*/}
      </div>
    </section>
  </>

}


export default App
